import React, { useState, useEffect } from 'react';
import { SketchPicker } from 'react-color';

function OptionsCreo(props) {
  const [bgcolor,setColor] = useState('#FFF')

  useEffect(() => {
    setColor(bgcolor)
  }, [bgcolor])



  return (
    <div>
      <SketchPicker
              color={ bgcolor }
              onChangeComplete={ setColor }
              />
      <p>{ bgcolor }</p>
    </div>
  )
}

export default OptionsCreo;
