import React from 'react'

function hslToHex(h, s, l) {
  const a = s * Math.min(l, 1 - l) ;
  const f = n => {
    const k = (n + h / 30) % 12;
    const color = l - a * Math.max(Math.min(k - 3, 9 - k, 1), -1);
    return Math.round(255 * color).toString(16).padStart(2, '0');   // convert to Hex and prefix "0" if needed
  };
  return `#${f(0)}${f(8)}${f(4)}`;
}

function Palette(props) {
  let color1 = `#ffffff`
  let color2 = `#ffffff`
  let color3 = `#ffffff`
  let color4 = `#ffffff`
  let color5 = `#ffffff`

  let paletteMethod= 2

  let rule1 = 2

  switch(paletteMethod) {
    case 2:
      rule1 = 180
      rule2 = 180
    case 3:
    case 4:
    case 5:
  }

  color1 = hslToHex(props.mainhue, props.mainsat, props.mainlight)
  color2 = hslToHex(props.mainhue+rule1, props.mainsat, props.mainlight)
  color3 = hslToHex(props.mainhue+(2*rule1), props.mainsat, props.mainlight)
  color4 = hslToHex(props.mainhue+(rule2), props.mainsat, props.mainlight)
  color5 = hslToHex(props.mainhue+(2*rule2), props.mainsat, props.mainlight)


  return (
    <div className="palette-container">
      <div className="color-pan" style={{ backgroundColor: color1 }}><p>{ color1 }</p></div>
      <div className="color-pan" style={{ backgroundColor: color2 }}><p>{ color2 }</p></div>
      <div className="color-pan" style={{ backgroundColor: color3 }}><p>{ color3 }</p></div>
      <div className="color-pan" style={{ backgroundColor: color4 }}><p>{ color4 }</p></div>
      <div className="color-pan" style={{ backgroundColor: color5 }}><p>{ color5 }</p></div>
    </div>
  )
}

export default Palette;
