import React from 'react'

function Palette(props) {
    function hslToHex(h, s, l) {
      l /= 100;
      const a = s * Math.min(l, 1 - l) / 100;
      const f = n => {
        const k = (n + h / 30) % 12;
        const color = l - a * Math.max(Math.min(k - 3, 9 - k, 1), -1);
        return Math.round(255 * color).toString(16).padStart(2, '0');   // convert to Hex and prefix "0" if needed
      };
      return `#${f(0)}${f(8)}${f(4)}`;
  }

  let paletteMethod = 3

  switch(paletteMethod) {
    case 2:
      return (
        <div>
          <h2>Complementary</h2>
          <div className="palette-container">
            <div className="color-pan" style={{ backgroundColor: `hsl(${props.mainhue}, ${props.mainsat*100}%, ${props.mainlight*100}%)` }}></div>
            <div className="color-pan" style={{ backgroundColor: `hsl(${props.mainhue+180}, ${props.mainsat*100}%, ${props.mainlight*100}%)` }}></div>
          </div>
        </div>
      )
    case 3:
      return (
        <div className="palette-container">
          <div className="color-pan" style={{ backgroundColor: `hsl(${props.mainhue}, ${props.mainsat*100}%, ${props.mainlight*100}%)` }}></div>
          <div className="color-pan" style={{ backgroundColor: `hsl(${props.mainhue+120}, ${props.mainsat*100}%, ${props.mainlight*100}%)` }}></div>
          <div className="color-pan" style={{ backgroundColor: `hsl(${props.mainhue+240}, ${props.mainsat*100}%, ${props.mainlight*100}%)` }}></div>
        </div>
      )
    case 4:
      return (
        <div className="palette-container">
          <div className="color-pan"></div>
          <div className="color-pan"></div>
          <div className="color-pan"></div>
          <div className="color-pan"></div>
        </div>
      )
    case 5:
      return (
        <div className="palette-container">
          <div className="color-pan"></div>
          <div className="color-pan"></div>
          <div className="color-pan"></div>
          <div className="color-pan"></div>
          <div className="color-pan"></div>
        </div>
      )
    default:
      return (
        <div className="palette-container">
          <div></div>
          <div></div>
          <div></div>
        </div>
      )
    }
}

export default Palette;
