import React from 'react'
import Palette from './Palette'
import OptionsCreo from './OptionsCreo'

class Creo extends React.Component {
  state = { color: "#FFF" }

    changeDivColor = (bgcolor) => {
      this.setState({color:bgcolor})
    }

  render() {
  return(
    <div>
      <p>{ this.state.color }</p>
      <Palette />
      <OptionsCreo parentcolor={this.changeDivColor} />
    </div>
  )
}
}

export default Creo;
