import React from 'react'
import Palette from './Palette'
import OptionsCreo from './OptionsCreo'

class Creo extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      color:"#FFFFFF",
      hue: 0,
    }
  }

  changeDivColor = (bgcolor, bghue) => {
    this.setState({
      color: bgcolor,
      hue: bghue,
    })
  }

  render() {
    return(
      <div>
        <p>{ this.state.hue }</p>
        <Palette mainColor={ this.state.color }/>
        <OptionsCreo onChange={this.changeDivColor} />
      </div>
    )
  }
}

export default Creo;
