import React from 'react'
import Palette from './Palette'
import OptionsCreo from './OptionsCreo'

class Creo extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      color:"#FFFFFF",
      hue: 255,
      sat: 255,
      lum: 255,
    }
  }

  changeDivColor = (bgcolor, bghue, bgsat, bglight) => {
    this.setState({
      color: bgcolor,
      hue: bghue,
      sat: bgsat,
      light: bglight,
    })
  }

  render() {
    return(
      <div>
        <p>{ this.state.hue }{ this.state.sat }{ this.state.light }</p>
        <Palette mainhue={ this.state.hue } mainsat={ this.state.light } mainlight={ this.state.light } />
        <OptionsCreo onChange={this.changeDivColor} />
      </div>
    )
  }
}

export default Creo;
