import React from 'react';
import { SketchPicker } from 'react-color';

class OptionsCreo extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      hex: '#FFFFFF',
      rgb: {
        r: 51,
        g: 51,
        b: 51,
        a: 1,
      },
      hsl: {
        h: 0,
        s: 0,
        l: .20,
        a: 1,
      },
    }
  }

  handleChange = (color) => {
    this.setState({
      hex: color.hex,
      rgb: {
        r: color.rgb.r,
        g: color.rgb.g,
        b: color.rgb.b,
        a: 1,
      },
      hsl: {
        h: color.hsl.h,
        s: color.hsl.s,
        l: color.hsl.l,
        a: 1,
      },
    })
    this.props.onChange(color.hex)
  }

  render() {
    return (
      <div>
        <SketchPicker
                color={ this.state.hex }
                onChange={ this.handleChange }
        />
        <div>
          <div style={{backgroundColor: this.state.hex, height:20, width:20}}></div>
          <p>{ this.state.hsl.h }</p>
        </div>
      </div>
    )
  }
}

export default OptionsCreo;
