import React from 'react'

function hslToHex(h, s, l) {
  const a = s * Math.min(l, 1 - l) ;
  const f = n => {
    const k = (n + h / 30) % 12;
    const color = l - a * Math.max(Math.min(k - 3, 9 - k, 1), -1);
    return Math.round(255 * color).toString(16).padStart(2, '0');   // convert to Hex and prefix "0" if needed
  };
  return `#${f(0)}${f(8)}${f(4)}`;
}

function Palette(props) {
  let firstC = `#ffffff`
  let secondC = `#ffffff`
  let thirdC = `#ffffff`

  let paletteMethod= 2

  let rule1 = 2

  switch(paletteMethod) {
    case 2:
      rule1 = 180
    case 3:
    case 4:
    case 5:
  }

  let color1 = hslToHex(props.mainhue, props.mainsat, props.mainlight)
  let color2 = hslToHex(props.mainhue+rule1, props.mainsat, props.mainlight)
  let color3 = hslToHex(props.mainhue+(2*rule1), props.mainsat, props.mainlight)
  let color4 = hslToHex(props.mainhue-(rule1), props.mainsat, props.mainlight)
  let color5 = hslToHex(props.mainhue-(2*rule1), props.mainsat, props.mainlight)


  return (
    <div className="palette-container">
      <div className="color-pan" style={{ backgroundColor: `hsl(${props.mainhue}, ${props.mainsat*100}%, ${props.mainlight*100}%)` }}><p>{ firstC }</p></div>
      <div className="color-pan" style={{ backgroundColor: `hsl(${props.mainhue+120}, ${props.mainsat*100}%, ${props.mainlight*100}%)` }}><p>{ secondC }</p></div>
      <div className="color-pan" style={{ backgroundColor: `hsl(${props.mainhue+240}, ${props.mainsat*100}%, ${props.mainlight*100}%)` }}><p>{ thirdC }</p></div>
    </div>
  )
}

export default Palette;
