import React, { useState, useEffect } from 'react';
import { SketchPicker } from 'react-color';

function OptionsCreo(props) {
  const [color,setColor] = useState('#FFF')

  useEffect(() => {
    setColor(color)
  }, color)



  return (
    <div>
      <SketchPicker
              color={ color }
              onChangeComplete={ setColor }
              />
      <p>{ color }</p>
    </div>
  )
}

export default OptionsCreo;
